resources.string:
attribute vec2 aPosition;
uniform vec2 uInterpolators;
uniform vec2 uPositions[2];
uniform vec2 uNormal;
void main() {
    vec2 curPos = mix(uPositions[0], uPositions[1], uInterpolators.x);
    vec2 endPos = mix(uPositions[0], uPositions[1], uInterpolators.y);
    curPos += (endPos - curPos) * aPosition.x;
    curPos += uNormal * aPosition.y;

    gl_Position = vec4(curPos, 0.0, 1.0);
}

precision mediump float;
uniform vec3 uColor;
void main() {
    gl_FragColor = vec4(uColor, 1.0);
}

attribute vec2 aPosition;
uniform vec2 uAspectRatio;
uniform vec2 uCenterPos;
uniform vec2 uVectorUp;
uniform float uScale;
varying vec2 vPosition;
void main() {
    /* Calculate normalized up and right vectors */
    vec2 vecUp = normalize(uVectorUp);
    vec2 vecRight = vec2(vecUp.y, -vecUp.x);

    /* Calculate box coordinates at origo */
    vec2 pos = (aPosition.x * vecRight) + (aPosition.y * vecUp);
    pos *= uAspectRatio * uScale;

    /* Translate box into its position */
    pos += (uCenterPos.x * vecRight) + (uCenterPos.y * vecUp);

    /* Store position */
    gl_Position = vec4(pos, 0.0, 1.0);
    vPosition = aPosition;
}

precision mediump float;
uniform vec3 uColor;
varying vec2 vPosition;
void main() {
    vec2 shadowPos = (abs(vPosition) - 0.95) * 20.0;
    shadowPos = max(shadowPos, vec2(0.0));

    if (shadowPos.x > 0.0 || shadowPos.y > 0.0) {
        float shadowT = clamp(length(shadowPos), 0.0, 1.0);
        shadowT = mix(0.2, 0.0, shadowT);
        gl_FragColor = vec4(vec3(0.0), shadowT);
    } else {
        gl_FragColor = vec4(uColor, 1.0);
    }
}

attribute vec2 aPosition;
varying vec2 vTextureCoord;
void main() {
    gl_Position = vec4(aPosition, 0.0, 1.0);
    vTextureCoord = (gl_Position.xy + 1.0) * 0.5;
}

precision mediump float;
uniform sampler2D sTextureBg;
uniform sampler2D sTextureFg;
varying vec2 vTextureCoord;
void main() {        
    const float sqrt2 = 1.4143;
    const float cornerBorderRadius = 1.2;
    const float cornerMaxLenInv = 1.0 / (sqrt2 - cornerBorderRadius);

    vec4 colorBg = texture2D(sTextureBg, vTextureCoord);
    vec4 colorFg = texture2D(sTextureFg, vTextureCoord);
    gl_FragColor = mix(colorBg, colorFg, colorFg.a);

    float cornerPos = length((vTextureCoord - 0.5) * 2.0);
    if (cornerPos > cornerBorderRadius) {
        float shade = (cornerPos - cornerBorderRadius) * cornerMaxLenInv;
        float shadeMain = mod(shade * 6.0, 6.0);
        float shadeBool = mod(shadeMain, 2.0);

        float t;
        if (clamp(shadeBool, 0.15, 1.0) == shadeBool) {
            t = 0.2;
        } else if (clamp(shadeBool, 1.15, 2.0) == shadeBool) {
            t = 1.0 - sqrt(shadeMain * 0.1);
        } else {
            t = (1.0 - sqrt(shadeMain * 0.1)) * 0.7;
        }

        gl_FragColor.rgb *= t;
    }
}
